mapper.select:
select * from sys_role


		role_id = #{id}


		and role_name like concat('5',#{name},'%')




		order by #{field} #{order}


		order by role_id asc



	limit #{pageNum},#{pageSize}

select count(*) from sys_role


		role_id = #{id}


		and role_name like concat('5',#{name},'%')

mapper.update:
update sys_role
role_name = #{roleName}, role_code = #{roleCode}, role_type = #{roleType}, status = #{status}, remark = #{remark}
where role_id = #{roleId}
