mapper.select:
select


		top ${pageSize} TEMP.*


		TEMP.*


	from (
		SELECT ROW_NUMBER() OVER (ORDER BY role_id asc) AS RowNumber,
		* FROM dbo.sys_role r


		r.level_num >= #{maxLevelNum}


		and role_name like '%'+#{queryName}+'%'


)AS TEMP

	WHERE RowNumber > ${pageSize}*${pageNum}

mapper.update:
update sys_role 

	role_name = #{roleName}, 
	role_num = #{roleNum}, 
	level_num = #{levelNum}, 
	remark = #{remark}

where role_id = #{roleId}

mapper.delete:
delete from sys_user_role where role_id in

	#{roleId}

;
delete from sys_role_menu where role_id in

	#{roleId}

;
delete from sys_role where role_id in 

	#{roleId}
