mapper.select:
select u.*,
	(SELECT d.name FROM sys_dept d WHERE d.dept_id = u.dept_id) as deptName
from sys_user u where user_id = #{value}

select


		top ${pageSize} A.*,


		A.*,


(select d.name from sys_dept d where d.dept_id = A.dept_id) as deptName
from  (
	SELECT ROW_NUMBER() OVER (ORDER BY user_id) AS RowNumber,* FROM dbo.sys_user u

		${filterSql}

			AND u.dept_id IN (${childDeptIds})


			AND u.cname like '%'+#{queryName}+'%'


)as A


		RowNumber > ${pageSize}*${pageNum}

select distinct rm.menu_id from sys_user_role ur
	LEFT JOIN sys_role_menu rm on ur.role_id = rm.role_id
where ur.user_id = #{userId}

mapper.update:
update sys_user
username = #{username}, cname = #{cname}, password = #{password}, email = #{email}, mobile = #{mobile}, status = #{status}, dept_id = #{deptId},
where user_id = #{userId}

mapper.delete:
delete from sys_user_role where user_id in

	#{userId}

delete from sys_user where user_id in

	#{userId}
